import { useParams, useNavigate } from "react-router-dom";
import TextField from "./TextField";
import AddBtn from "./AddBtn";
import { Formik, Form } from "formik";
import * as yup from "yup";
import { useEffect, useState } from "react";
import axios from "axios";

const EditBlog = () => {
  const validates = yup.object().shape({
    blog_title: yup
      .string()
      .required("Title Is Required")
      .max(120, "Must be 120 or less letters ")
      .min(10),
    blog_content: yup
      .string()
      .required("Content Is Required")
      .max(520, "Must be 500 or less letters ")
      .min(10),
  });
  const navigate = useNavigate();

  const { id } = useParams();
  const [blogData, setBlogData] = useState({
    blog_title: "",
    blog_content: "",
  });

  useEffect(() => {
    axios
      .get(`http://localhost:8081/api/blogList/${id}`)
      .then((response) => {
       var blog_titles = response.data.blog_title;
        var blog_contents= response.data.blog_content;
        // setBlogData(

        // );
      })
      .then((error) => {
        console.error(error);
      });
  },[id]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setBlogData({
      ...blogData,
      [name]: value,
    });
  };



  const handleUpdateBlog = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.put(
        `http://localhost:8081/api/updateBlog/${id}`,
        e
      );
      console.log(response.data);
      setBlogData({
        blog_title: "",
        blog_content: "",
      });
      navigate("/blogList");
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <>
      <h1 className="text-center m-5">EDIT BLOG</h1>
      <Formik
         initialValues={{ blog_title:blog_titles, blog_content:blog_contents }}
        validationSchema={validates}
        // onSubmit={handleUpdateBlog}
      >
        {() => (
          <div className="container m-5">
            <Form>
              <div className="row justify-content-center">
                <div className="form-group col-md-4 col-md-offset-5 align-center ">
                  <TextField
                    label="Title: "
                    type="text"
                    name="blog_title"

                  />
                  <TextField
                    label="Content: "
                    type="text"
                    name="blog_content"
                  />
                  <div>
                    <AddBtn
                      onClick={handleUpdateBlog}
                      value={"updateBlog"}
                      name="UpdateBlog"
                      style={{
                        marginLeft: "64px",
                        marginTop: "20px",
                        backgroundColor: "#1070ef",
                        color: "white",
                        width: "99px",
                      }}
                      type="submit"
                    >
                      {" "}
                      Edit Blog
                    </AddBtn>
                  </div>
                </div>
              </div>
            </Form>
          </div>
        )}
      </Formik>
    </>
  );
};

export default EditBlog;
