import React from "react";
import { useSelector } from "react-redux";


const BlogList = () => {
 const blogs = useSelector((store) => store.blogs);

//   var data = JSON.parse(localStorage.getItem("blogs")) || [];
// console.log(data);
// var blogs = data.find(function (blog) {
//   console.log(blog.title);
//   return blog.title;
// })


  const renderBlogs = () =>
    blogs.map((blog) => (
      <div key={blog.id}>
        <div className="card m-2" style={{ width: "20rem" }}>
          <div className="card-body">
            <h5 className="card-title">{blog.title}</h5>
            <p className="card-text">{blog.content}</p>
          </div>
        </div>
      </div>
    )); 

  return (
    <>
      <h1 style={{ color: "black" }} className="text-center m-3">
        Blogs
      </h1>{" "}
      {/* <div className="text-center d-flex flex-wrap m-3 p-3 justify-content-center">
        {blogs.length ? renderBlogs() : <p>No Blogs</p>}
      </div> */}
    </>
  );
};

export default BlogList;
