import React from "react";
import { AiOutlineDelete } from "react-icons/ai";
import { CiEdit } from "react-icons/ci";
import AddBtn from "./AddBtn";
import { Link } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { deleteBlog } from "../BlogReducer";

const BlogList = () => {
  const blogs = useSelector((store) => store.blogs);
  const dispatch = useDispatch();

  const handleRemoveBlog = (id) => {
    dispatch(deleteBlog(id));
  };

  const renderBlogs = () =>
    blogs.map((blog) => (
      <div key={blog.id}>
        <div className="card m-2" style={{ width: "20rem" }}>
          <div className="card-body">
            <h5 className="card-title">{blog.title}</h5>
            <p className="card-text">{blog.content}</p>
            <Link
              to={`/Edit/${blog.id}`}
              style={{ margin: "15px", color: "green", fontSize: "20px" }}
            >
              <CiEdit />
            </Link>
            <Link

              style={{ color: "red", fontSize: "20px" }}
              data-bs-toggle="modal"
              data-bs-target="#exampleModal"
            >
              <AiOutlineDelete />
            </Link>
          </div>
        </div>
        <div
        class="modal fade"
        id="exampleModal"
        tabindex="-1"
        aria-labelledby="exampleModalLabel"
        aria-hidden="true"
      >
        <div class="modal-dialog">
          <div class="modal-content">
            <div class="modal-header">
              <h5 class="modal-title" id="exampleModalLabel">
                Delete BLog
              </h5>
              <button
                type="button"
                class="btn-close"
                data-bs-dismiss="modal"
                aria-label="Close"
              ></button>
            </div>
            <div class="modal-body">Are You Sure ? You Want To Delete This Blog ??</div>
            <div class="modal-footer">
              <button
                type="button"
                class="btn btn-secondary"
                data-bs-dismiss="modal"
                onClick={() => handleRemoveBlog(blog.id)}
              >
              YES
              </button>
              <button type="button" class="btn btn-primary btn-close"
                data-bs-dismiss="modal"
                aria-label="Close">
               No
              </button>
            </div>
          </div>
        </div>
      </div>
      </div>
    ));

  return (
    <>
      <h1 style={{ color: "black" }} className="text-center m-3">
        Blogs
      </h1>{" "}
      <Link to="/AddBlog">
        <AddBtn
          style={{
            marginLeft: "145px",
            backgroundColor: "#1f813a",
            color: "white",
            marginTop: "15px",
          }}
        >
          {" "}
          Add Blog
        </AddBtn>
      </Link>
      <div className="text-center d-flex flex-wrap m-3 p-3 justify-content-center">
        {blogs.length ? renderBlogs() : <p>No Blogs</p>}
      </div>

    </>
  );
};

export default BlogList;
