import React from "react";
import { AiOutlineDelete } from "react-icons/ai";
import { CiEdit } from "react-icons/ci";
import AddBtn from "./AddBtn";
import { Link } from "react-router-dom";


const BlogList = () => {
  const blogs = [
    {
      title: "js",
      content: "jsssssss",
    },
    {
      title: "js",
      content: "jsssssss",
    },
    {
      title: "js",
      content: "jsssssss",
    },
    {
      title: "js",
      content: "jsssssss",
    },
    {
      title: "js",
      content: "jsssssss",
    },
    {
      title: "jjsssssssjsssssssjsssssssjsssssssjsssssssjsssssssjssssssss",
      content: "jjsssssssjsssssssjssssssssssssss",
    },
    {
      title:
        "jjsssssssjsssssssjsssssssjsssssssjsssssssjsssssssjsssssssjsssssssjsssssssjssssssss",
      content:
        "jsjsssssssjsssssssjsssssssjsssssssjsssssssjsssssssjsssssssjsssssssssssss",
    },
    {
      title: "jsjsssssssjsssssssjsssssssjsssssss",
      content: "jsssssssjsssssssjsssssssjsssssssjsssssss",
    },
    {
      title: "jsfgerg",
      content: "jsssssssgrh",
    },
    {
      title: "jthhs",
      content: "jssstrhtssss",
    },
    {
      title: "jstyjrtj",
      content: "jsssjttttthtrjht",
    },
  ];
  const blog = () =>
    blogs.map((blog) => (
      <div>
        <div className="card m-2" style={{ width: "20rem" }} key={blogs.id}>
          <div className="card-body">
            <h5 className="card-title">{blog.title}</h5>
            <p className="card-text">{blog.content}</p>
            <a href="#" className="card-link">
              <AiOutlineDelete />
            </a>
            <a href="#" className="card-link">
              <CiEdit />
            </a>
          </div>
        </div>
      </div>
    ));

  return (
    <>
      <h1 style={{ color: "black" }} className="text-center m-3">
        Blogs
      </h1>{" "}
      <Link to="/AddBlog"> 
        <AddBtn
          style={{
            marginLeft: "145px",
            backgroundColor: "#1f813a",
            color: "white",
            marginTop: "15px",
          }}
        >
          {" "}
          Add Blog
        </AddBtn>
      </Link>
      <div className="text-center d-flex flex-wrap m-3 p-3 justify-content-center">
        {blogs.length ? blog() : <p>NoBlogs</p>}
      </div>
    </>
  );
};

export default BlogList;
