import { createSlice } from "@reduxjs/toolkit";

const initialState = [];

const userSlice = createSlice({
  name: "user",
  initialState,
  reducers: {
    signUP: (state, action) => {
        const newUSer = {
            ...action.payload,
            ...state

          };
          state.push(newUSer);
    }
  },
});

export const { signUP , logIn } = userSlice.actions;
export default userSlice.reducer;
