import React, { useState } from "react";
import "./LoginForm.module.css";
import TextField from "./TextField";
import AddBtn from "./AddBtn";
import { useNavigate } from "react-router-dom";

const LoginForm = () => {

  const navigate = useNavigate();
  const [values, setValues] = useState({
    email: "",
    password: "",
  });

  const handleLogin = (e) => {
    e.preventDefault();
    if (!values.email || !values.password) {
      alert("Please fill in all fields");
      return;
    }
    var users = JSON.parse(localStorage.getItem('users')) || [];
    sessionStorage.setItem('ce', values.email)

    // Find the user with the matching email
    var user = users.find(function (user) {
      return user.email === values.email;

    });

    if (user) {
      // Check if passwords match
      if (user.password === btoa(values.password)) {
        alert('Login successful for ' + user.name + '!');
        if(user.role === 'user'){
          sessionStorage.setItem('name', values.name)
          navigate('/Blogs')
        }else{
          navigate('/BlogList')
          sessionStorage.setItem('name', values.name)
        }
      }
    }
  };



  return (
    <>

      <form style={{ marginLeft: "554px", marginTop: "40px", width: "240px" }}>
        <h1>Login</h1>
        <div className="mb-3">
          <TextField
            label="Email: "
            value={values.email}
            onChange={(e) => setValues({ ...values, email: e.target.value })}
            inputs={{ type: "text", required: true, pattern: '^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$' }}
            errorMessage="Enter valid email"
          />
        </div>
        <div className="mb-3">
          <TextField
            label="Password: "
            value={values.password}
            onChange={(e) => setValues({ ...values, password: e.target.value })}
            inputs={{ type: "password", required: true, pattern: "/^(?=.*[0-9])(?=.*[a-z])(?=.*[A-Z])(?=.*_)(?!.*\W)(?!.* ).{8,16}$/" }}
            errorMessage="Password must contain digits,lowercase and uppercase letter, one underscore but no other special character and space and it must be 8-16 characters long"

          />
        </div>
        <div>
          <AddBtn
            style={{
              marginLeft: "64px",
              marginTop: "20px",
              backgroundColor: "#1070ef",
              color: "white",
              width: "99px",
            }}
            onClick={handleLogin}
          >
            {" "}
            Login
          </AddBtn>
        </div>
      </form>
    </>
  );
};

export default LoginForm;
