import { useParams, useNavigate } from "react-router-dom";
import TextField from "./TextField";
import AddBtn from "./AddBtn";
import { Formik, Form } from "formik";
import * as yup from "yup";
import { useEffect, useState } from "react";
import axios from "axios";

const EditBlog = () => {
  const validates = yup.object().shape({
    blog_title: yup
      .string()
      .required("Title Is Required")
      .max(120, "Must be 120 or less letters ")
      .min(10),
    blog_content: yup
      .string()
      .required("Content Is Required")
      .max(520, "Must be 500 or less letters ")
      .min(10),
  });

  const {id} = useParams();
  const[blogData,setBlogData] = useState({
    blog_title:'',
    blog_content:''
  })

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setBlogData({
      ...blogData,
      [name]: value,
    });
  };

  useEffect(()=>{
    axios.get(`http://localhost:8081/api/blogList/${id}`)
    .then((response)=>{
      setBlogData({
        ...blogData,
        'blog_title':response.data.blog_title,
        'blog_content':response.data.blog_content
      })
    })
  })

  const navigate = useNavigate();

  const data = JSON.parse(localStorage.getItem("blogs"));
  const blog = data.find((blog) => blog.id === parseInt(params.id));
  if (blog) {
    var t = blog.title;
    var c = blog.content;
  }

  const handleUpdateBlog = (values) => {
    // dispatch(
    //   editBlog({
    //     id: parseInt(params.id),
    //     title: values.title,
    //     content: values.content,
    //   })
    // );
    alert("Blog Updated!!");
    navigate("/BlogList");
  };

  return (
    <>
      <h1 className="text-center m-5">EDIT BLOG</h1>
      <Formik
        initialValues={{ title: t, content: c }}
        validationSchema={validates}
        // onSubmit={handleUpdateBlog}
      >
        {() => (
          <div className="container m-5">
            <Form>
              <div className="row justify-content-center">
                <div className="form-group col-md-4 col-md-offset-5 align-center ">
                  <TextField label="Title: " type="text" name="blog_title"  value={blogData.blog_title}  onChange={handleInputChange}/>
                  <TextField label="Content: " type="text" name="blog_content"  value={blogData.blog_content}  onChange={handleInputChange}/>
                  <div>
                    <AddBtn
                    onClick={handleUpdateBlog}
                      style={{
                        marginLeft: "64px",
                        marginTop: "20px",
                        backgroundColor: "#1070ef",
                        color: "white",
                        width: "99px",
                      }}
                      type="submit"
                    >
                      {" "}
                      Edit Blog
                    </AddBtn>
                  </div>
                </div>
              </div>
            </Form>
          </div>
        )}
      </Formik>
    </>
  );
};

export default EditBlog;
