import React, { useState } from "react";
import TextField from "./TextField";
import AddBtn from "./AddBtn";
import { useNavigate } from "react-router-dom";
import SelectField from "./SelectField";
import { signUP } from "../AuthReducer";

const SignupForm = () => {
  const navigate = useNavigate();

  const [values, setValues] = useState({
    name: "",
    email: "",
    password: "",
    role: "",
  });

  const options = [
    { value: "user", label: "User" },
    { value: "admin", label: "Admin" },
  ];

  const handleSignup = (e) => {
    e.preventDefault();
    if (!values.name || !values.email || !values.password || !values.role) {
      alert("Please fill in all fields");
      return;
    }
    alert("Sign up successful!");
    dispatch(
      signUP({
        title: values.title,
        content: values.content,
      })
    );
    navigate("/LoginForm");
    var users = JSON.parse(localStorage.getItem("users")) || [];
    users.push({
      name: values.name,
      email: values.email,
      password: btoa(values.password),
      role: values.role,
    });

    // Store the updated array back into localStorage
    localStorage.setItem("users", JSON.stringify(users));
  };

  return (
    <>
      <form style={{ marginLeft: "554px", marginTop: "40px", width: "240px" }}>
        <h1>SignUp</h1>
        <div className="mb-3">
          <TextField
            label="Name: "
            value={values.name}
            onChange={(e) => setValues({ ...values, name: e.target.value })}
            inputs={{
              type: "text",
              required: true,
              pattern: "^[A-Za-z]{3,16}$",
            }}
            errorMessage="Name have at-least 2 to 10 character and do not use any special character in name"
          />
          <TextField
            label="Email: "
            value={values.email}
            onChange={(e) => setValues({ ...values, email: e.target.value })}
            inputs={{
              type: "text",
              required: true,
              pattern: "^[A-Z0-9._%+-]+@[A-Z0-9.-]+.[A-Z]{2,4}$",
            }}
            errorMessage="Enter valid email"
            required={true}
          />
        </div>
        <div className="mb-3">
          <TextField
            label="Password: "
            value={values.password}
            onChange={(e) => setValues({ ...values, password: e.target.value })}
            inputs={{
              type: "password",
              required: true,
              pattern:
                "/^(?=.*[0-9])(?=.*[a-z])(?=.*[A-Z])(?=.*_)(?!.*W)(?!.* ).{8,16}$/",
            }}
            errorMessage="Password must contain digits,lowercase and uppercase letter, one underscore but no other special character and space and it must be 8-16 characters long"
          />
        </div>
        <div className="mb-3">
          <SelectField
            label={options.label}
            options={options}
            value={values.role}
            onChange={(e) => setValues({ ...values, role: e.target.value })}
          />
        </div>
        <div>
          <AddBtn
            style={{
              marginLeft: "64px",
              marginTop: "20px",
              backgroundColor: "#1070ef",
              color: "white",
              width: "99px",
            }}
            onClick={handleSignup}
          >
            {" "}
            SignUP
          </AddBtn>
        </div>
      </form>
    </>
  );
};

export default SignupForm;
