import React, { useState } from "react";
import './form.css'

const TextField = ({ label, inputs, onChange, value,errorMessage}) => {

  const [focused,serFocused] = useState(false)

  const handleFocus = () =>{
    serFocused(true)
  }

  return (
    <>
      <div>
        <label className="form-label ">{label}</label>
        <input
          className="form-control inputError"
          style={{ width: "210px" }}
          {...inputs}
          onChange={onChange}
          value={value} 
          onBlur={handleFocus}
          focused={focused.toString()}
        />
        <span>{errorMessage}</span>
      </div>
    </>
  );
};

export default TextField;
