import React from "react";
import { AiOutlineDelete } from "react-icons/ai";
import { CiEdit } from "react-icons/ci";

const BlogList = () => {
  const blogs = [
    {
      title: "js",
      content: "jsssssss",
    },
    {
      title: "js",
      content: "jsssssss",
    },
  ];
  const blog = () =>
    blogs.map((blog) => (
      <>
        {/* <div className="d-flex align-items-center ">
          <div>
            <h2>{blog.title}</h2>
            <p>{blog.content}</p>
          </div>
          <div>
            <div>
              <AiOutlineDelete />
              <CiEdit />
            </div>
          </div>
        </div> */}
        <div class="row">
          <div class="col-sm-6">
            <div class="card">
              <div class="card-body">
                <h5 class="card-title">{blog.title}</h5>
                <p class="card-text">{blog.content}</p>
                <a href="#" class="btn btn-primary">
                  <AiOutlineDelete />
                </a>
                <a href="#" class="btn btn-primary">
                  <CiEdit />
                </a>
              </div>
            </div>
          </div>
        </div>
      </>
    ));

  return (
    <>
      <h1 style={{ color: "black" }}>Blogs</h1>
      <div className="row">
        <div className="col-sm-6">{blogs.length ? blog() : <p>NoBlogs</p>}</div>
      </div>
    </>
  );
};

export default BlogList;
