// BlogList.js

import { AiOutlineDelete } from "react-icons/ai";
import { CiEdit } from "react-icons/ci";
import AddBtn from "./AddBtn";
import { Link } from "react-router-dom";
import LogoutBtn from "./LogoutBtn";
import { useEffect, useState } from "react";
import axios from "axios";

const BlogList = () => {
  const [blogData, setBlogData] = useState([]);

  useEffect(() => {
    getBlog();
  }, []);

  const getBlog = async () => {
    try {
      const response = await axios.get("http://localhost:8081/api/blogs/blogList");
      setBlogData(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  const handleRemoveBlog = async (id) => {
    try {
      await axios.delete(`http://localhost:8081/api/blogs/deleteBlog/${id}`);
      getBlog();
    } catch (error) {
      console.error(error);
    }
  };

  const renderBlogs = () =>
    blogData.map((blog) => (
      <div key={blog._id}>
        <div className="card m-2" style={{ width: "20rem" }}>
          <div className="card-body">
            <h5 className="card-title">{blog.blog_title}</h5>
            <p className="card-text">{blog.blog_content}</p>
            <img src={`data:image/jpeg;base64,${blog.blog_img_base64}`} alt="Blog Image" className="card-img-top" />
            <Link
              to={`/updateBlog/${blog._id}`}
              style={{ margin: "15px", color: "green", fontSize: "20px" }}
            >
              <CiEdit />
            </Link>
            <Link
              style={{ color: "red", fontSize: "20px" }}
              data-bs-toggle="modal"
              data-bs-target="#exampleModal"
            >
              <AiOutlineDelete onClick={() => handleRemoveBlog(blog._id)} />
            </Link>
          </div>
        </div>
      </div>
    ));

  return (
    <>
      <div className="justify-content-between m-5">
        <div className="row ">
          <div className="col-10">
            {/* <h3 className="">Welcome {token.name.toUpperCase()}</h3> */}
          </div>
          <div className="col-2">
            <Link to="/LoginForm">
              <LogoutBtn />
            </Link>
          </div>
        </div>
      </div>
      <h1 style={{ color: "black" }} className="text-center m-3">
        Blogs
      </h1>
      <Link to="/AddBlog">
        <AddBtn
          style={{
            marginLeft: "145px",
            backgroundColor: "#1f813a",
            color: "white",
            marginTop: "15px",
          }}
        >
          {" "}
          Add Blog
        </AddBtn>
      </Link>
      <div className="text-center d-flex flex-wrap m-3 p-3 justify-content-center">
        {blogData.length > 0 ? (
          renderBlogs()
        ) : (
          <p>No Blogs</p>
        )}
      </div>
    </>
  );
};

export default BlogList;
