import TextField from "./TextField";
import AddBtn from "./AddBtn";
import { useNavigate } from "react-router-dom";
import { Formik, Form } from "formik";
import * as yup from "yup";
import axios from "axios";
import { useState } from "react";

const AddBlog = () => {
  const validates = yup.object().shape({
    blog_title: yup
      .string()
      .required("Title Is Required")
      .max(120, "Title Must be 120 or less letters ")
      .min(10),
    blog_content: yup
      .string()
      .required("Content Is Required")
      .max(520, "blog Must be 500 or less letters ")
      .min(10),
  });

  const navigate = useNavigate();
  const [image, setImage] = useState(null);

  const handleAddBlog = async (values, formData) => {
    try {
      formData.append("blog_title", values.blog_title);
      formData.append("blog_content", values.blog_content);

      const response = await axios.post(
        "http://localhost:8081/api/blogs/addBlog",
        formData,
        {
          headers: { "Content-Type": "multipart/form-data" },
        }
      );
      console.log(response.data);
      navigate("/BlogList");
    } catch (error) {
      console.error(error);
    }
  };

  const onImageUpdate = (e) => {
    setImage(e.target.files[0]);
  };

  return (
    <>
      <h1 className="text-center m-5">ADD BLOG</h1>
      <Formik
        initialValues={{
          blog_title: "",
          blog_content: "",
        }}
        validationSchema={validates}
        onSubmit={(values) => {
          const formData = new FormData();
          formData.append("blog_img", image);
          handleAddBlog(values, formData);
        }}
      >
        {(formik) => (
          <div className="container m-5">
            <Form>
              <div className="row justify-content-center">
                <div className="form-group col-md-4 col-md-offset-5 align-center ">
                  <TextField label="Title: " type="text" name="blog_title" />
                  <TextField
                    label="Content: "
                    type="text"
                    name="blog_content"
                  />
                  <TextField
                    label="Image: "
                    type="file"
                    name="blog_img"
                    onChange={onImageUpdate}
                  />
                  <div>
                    <AddBtn
                      type="submit"
                      style={{
                        marginLeft: "64px",
                        marginTop: "20px",
                        backgroundColor: "#1070ef",
                        color: "white",
                        width: "99px",
                      }}
                    >
                      {" "}
                      Add Blog
                    </AddBtn>
                  </div>
                </div>
              </div>
            </Form>
          </div>
        )}
      </Formik>
    </>
  );
};

export default AddBlog;
