import React from "react";
import ReactDOM from "react-dom/client";
import "./index.css";
import App from "./App";
import reportWebVitals from "./reportWebVitals";
import axios from "axios";

const axiosInstance = axios.create({
  baseURL: "http://localhost:8081/api/login",
});

axiosInstance.interceptors.request.use(
  (config) => {
    const token = JSON.parse(localStorage.getItem("login"));
    if (token) {
      config.headers.Authorization = `Bearer ${token.refreshToken}`;
      console.log(token.refreshToken);
    }
    return config;
  },
  (error) => {
    return Promise.reject(error);
  }
);

axios.interceptors.response.use(
  (response) => {
    console.log("RES", response);
    return response;
  },
  async (error) => {
    if (error && error.response) {
      localStorage.removeItem("login");
      window.location = "/loginForm";
      console.log("ERR", error);
      console.log("ERR.RES", error.response);
    }
    const originalReq = error.config;
    if (error.response.status === 419 && !originalReq._r) {
      originalReq._r = true;
      return axiosInstance.post("/blogList", {
        refreshToken: JSON.parse(localStorage.getItem("login")).then((res) => {
          if (res.status === 200) {
            localStorage.setItem("token", res.data.token);
            return axiosInstance(originalReq);
          }
        }),
      });
    }

    return Promise.reject(error);
  }
);

const root = ReactDOM.createRoot(document.getElementById("root"));
root.render(
  <React.StrictMode>
    <App />
  </React.StrictMode>
);

// If you want to start measuring performance in your app, pass a function
// to log results (for example: reportWebVitals(console.log))
// or send to an analytics endpoint. Learn more: https://bit.ly/CRA-vitals
reportWebVitals();
