import React, { useState } from "react";
import "./LoginForm.module.css";
import TextField from "./TextField";
import AddBtn from "./AddBtn";
import { useNavigate } from "react-router-dom";
import { Formik, Form } from "formik";
import * as yup from "yup";



const LoginForm = () => {
  const validate = yup.object({
    name: yup
      .string()
      .required("Name Is Required")
      .max(10, "Must be 10 or less letters "),
    email: yup.string().email("invalid email").required("email is required"),
    password: yup
      .string()
      .required("Password is Required")
      .min(6, "At Least 6 Character"),
    //confirmPassword: yup.string().oneOf([yup.ref('password'), null], 'Passwords must match'),
  });
  const navigate = useNavigate()

  const handleLogin = (e) => {
    e.preventDefault();
    if (!values.email || !values.password) {
      alert("Please fill in all fields");
      return;
    }
    var users = JSON.parse(localStorage.getItem('users')) || [];
    sessionStorage.setItem('ce', values.email)


    // Find the user with the matching email
    var user = users.find(function (user) {
      return user.email === values.email;

    });

    if (user) {
      // Check if passwords match
      if (user.password === btoa(values.password)) {
        alert('Login successful for ' + user.name + '!');
        if(user.role === 'user'){

          navigate('/Blogs')
        }else{
          navigate('/BlogList')
        }
      }
    }
  };



  return (
    <>
 <Formik
        initialValues={{
          name: "",
          email: "",
          password: "",
          role: "",
        }}
        validationSchema={validate}
      >
        {(Formik) => (
          <div className="container m-5">
            <Form>
              <div className="row justify-content-center">
                <div className="form-group col-md-4 col-md-offset-5 align-center ">
                  <h1 className="m-5 text-center">Login </h1>
                  <TextField label="Email: " type="email" name="email" />
                  <TextField
                    label="Password: "
                    type="password"
                    name="password"
                  />
                  <div>
                    <AddBtn
                      style={{
                        marginLeft: "64px",
                        marginTop: "20px",
                        backgroundColor: "#1070ef",
                        color: "white",
                        width: "99px",
                      }}
                      onClick={handleLogin}
                    >
                      {" "}
                   LogIn
                    </AddBtn>
                  </div>
                </div>
              </div>
            </Form>
          </div>
        )}
      </Formik>
    </>
  );
};

export default LoginForm;
