// import React, { useEffect, useState } from "react";
import { AiOutlineDelete } from "react-icons/ai";
import { CiEdit } from "react-icons/ci";
import { Link } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { deleteBlog } from "../BlogReducer";
import LogoutBtn from "./LogoutBtn";

const BlogList = () => {
  const blogs = useSelector((store) => store.blogs);
  const dispatch = useDispatch();

  const handleRemoveBlog = (id) => {
    dispatch(deleteBlog(id));
  };

  var data = JSON.parse(localStorage.getItem('blogs'))
  console.log(data);
  console.log(data.title);
  var detail = data.title;
  console.log('title', detail);

  // var val = localStorage.getItem("blogs");
  // var object = JSON.parse(val);
  // console.log("title: ", object.title);
  // console.log("content: ", object.content);

  const renderBlogs = () =>
    blogs.map((blog) => (
      <div key={blog.id}>
        <div className="card m-2" style={{ width: "20rem" }}>
          <div className="card-body">
            <h5 className="card-title">{blog.title}</h5>
            <p className="card-text">{blog.content}</p>

          </div>
        </div>
      </div>
    ));

  return (
    <>
      <h1 style={{ color: "black" }} className="text-center m-3">
        Blogs
      </h1>{" "}
      <Link to="/LoginForm">
        <LogoutBtn />
      </Link>
      <div className="text-center d-flex flex-wrap m-3 p-3 justify-content-center">
        {blogs.length ? renderBlogs() : <p>No Blogs</p>}
      </div>
    </>
  );
};

export default BlogList;
