import React from "react";
import { AiOutlineDelete } from "react-icons/ai";
import { CiEdit } from "react-icons/ci";
import AddBtn from "./AddBtn";
import { Link } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { deleteBlog } from '../BlogReducer';

const BlogList = () => {
  const blogs = useSelector(store => store.blogs);
  const dispatch = useDispatch();

  const handleRemoveBlog = () => {
    dispatch(deleteBlog());
  };

  const renderBlogs = () => (
    blogs.map((blog) => (
      <div key={blog.id}>
        <div className="card m-2" style={{ width: "20rem" }}>
          <div className="card-body">
            <h5 className="card-title">{blog.title}</h5>
            <p className="card-text">{blog.content}</p>
            <Link to={`/Edit/${blog.id}`} style={{margin:'15px',color:'green',fontSize:'20px'}}>
              <CiEdit />
            </Link>
            <Link onClick={() => handleRemoveBlog(blog.id)} style={{color:'red',fontSize:'20px'}}>
              <AiOutlineDelete />
            </Link>
          </div>
        </div>
      </div>
    ))
  );

  return (
    <>
      <h1 style={{ color: "black" }} className="text-center m-3">
        Blogs
      </h1>{" "}
      <Link to="/AddBlog">
        <AddBtn
          style={{
            marginLeft: "145px",
            backgroundColor: "#1f813a",
            color: "white",
            marginTop: "15px",
          }}
        >
          {" "}
          Add Blog
        </AddBtn>
      </Link>
      <div className="text-center d-flex flex-wrap m-3 p-3 justify-content-center">
        {blogs.length ? renderBlogs() : <p>No Blogs</p>}
      </div>
    </>
  );
};

export default BlogList;
