import React, { useState } from "react";
import TextField from "./TextField";
import AddBtn from "./AddBtn";
import { useNavigate } from "react-router-dom";
import { useDispatch } from "react-redux";
import { addBlog } from "../BlogReducer";

const AddBlog = () => {
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const [values, setValues] = useState({
    title: "",
    content: "",
  });

  const handleAddBlog = (e) => {
    e.preventDefault();
    if (!values.title || !values.content) {
      alert("Please fill in all fields");
      return;
    }
    // Dispatching the new blog with the current values
    dispatch(
      addBlog({
        title: values.title,
        content: values.content,
      })

    );

    // Resetting the form values
    setValues({ title: "", content: "" });

    // Navigating to the BlogList page
    navigate("/BlogList");


    var allBogs = JSON.parse(localStorage.getItem("blogs")) || [];
    const showBlog = [...allBogs,blogs]
    blogs.push({
      title: values.title,
      content: values.content

    });

    // Store the updated array back into localStorage
    localStorage.setItem("blogs", JSON.stringify(showBlog));
  };

  return (
    <>
      <h1 className="text-center m-3">ADD BLOG</h1>
      <form style={{ marginLeft: "554px", marginTop: "40px" }}>
        <div className="mb-3">
          <TextField
            label="Title: "
            value={values.title}
            onChange={(e) => setValues({ ...values, title: e.target.value })}
            inputs={{ type: "text" }}
          />
        </div>
        <div className="mb-3">
          <TextField
            label="Content: "
            value={values.content}
            onChange={(e) => setValues({ ...values, content: e.target.value })}
            inputs={{ type: "text" }}
          />
        </div>
        <div>
          <AddBtn
            style={{
              marginLeft: "64px",
              marginTop: "20px",
              backgroundColor: "#1070ef",
              color: "white",
              width: "99px",
            }}
            onClick={handleAddBlog}
          >
            {" "}
            Add
          </AddBtn>
        </div>
      </form>
    </>
  );
};

export default AddBlog;
