import React, { useEffect, useState } from "react";
import { AiOutlineDelete } from "react-icons/ai";
import { CiEdit } from "react-icons/ci";
import AddBtn from "./AddBtn";
import { Link } from "react-router-dom";
import LogoutBtn from "./LogoutBtn";
// import axios from "axios";
import api from "../config";

// Create axios instance for specific base URL
const axiosInstance = axios.create({
  baseURL: "http://localhost:8081/api/",
});

// Add request interceptor for attaching token to requests
axiosInstance.interceptors.request.use(
  (config) => {
    const token = JSON.parse(localStorage.getItem("login"));
    if (token) {
      config.headers.Authorization = `Bearer ${token.token}`;
    }
    return config;
  },
  (error) => {
    return Promise.reject(error);
  }
);

// Add response interceptor for handling token expiration
axiosInstance.interceptors.response.use(
  (response) => {
    console.log("RES", response);
    return response;
  },
  async (error) => {
    if (error.response && error.response.status === 419) {
      console.log("Token expired, refreshing...");
      try {
        const refreshToken = JSON.parse(localStorage.getItem("login")).refreshToken;
        if (!refreshToken) {
          console.log("No refresh token provided.");
          throw new Error("No refresh token provided.");
        }
        const response = await api.get("api/bloglist", { refreshToken });
        if (response.status === 200) {
          localStorage.setItem("login", JSON.stringify(response.data));
          console.log('Token refreshed');
          return axiosInstance(error.config); // Retry the original request
        }
      } catch (refreshError) {
        console.error("Error refreshing token:", refreshError);
      }
    }
    return Promise.reject(error);
  }
);

const BlogList = () => {
  const [blogData, setBlogData] = useState([]);

  useEffect(() => {
    getBlog();
  }, []);

  const token = JSON.parse(localStorage.getItem("login"));

  const getBlog = async () => {
    try {
      const response = await axiosInstance.get("blogList");
      setBlogData(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  let selectedId = 0;

  const test = (id) => {
    selectedId = id;
  };

  const handleRemoveBlog = async (id) => {
    await axiosInstance.delete(`deleteBlog/${id}`);
    getBlog();
  };

  const renderBlogs = () =>
    blogData.map((blog) => (
      <div key={blog._id}>
        <div className="card m-2" style={{ width: "20rem" }}>
          <div className="card-body">
            <h5 className="card-img">
              <img src={`http://localhost:8081/uploads/${blog.blog_img}`} alt="" />
            </h5>
            <h5 className="card-title">{blog.blog_title}</h5>
            <p className="card-text">{blog.blog_content}</p>
            <Link
              to={`/updateBlog/${blog._id}`}
              style={{ margin: "15px", color: "green", fontSize: "20px" }}
            >
              <CiEdit />
            </Link>
            <Link
              style={{ color: "red", fontSize: "20px" }}
              data-bs-toggle="modal"
              data-bs-target="#exampleModal"
            >
              <AiOutlineDelete onClick={() => test(blog._id)} />
            </Link>
          </div>
        </div>
        <div
          className="modal fade"
          id="exampleModal"
          tabIndex="-1"
          aria-labelledby="exampleModalLabel"
          aria-hidden="true"
        >
          <div className="modal-dialog">
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title" id="exampleModalLabel">
                  Delete Blog
                </h5>
                <button
                  type="button"
                  className="btn-close"
                  data-bs-dismiss="modal"
                  aria-label="Close"
                ></button>
              </div>
              <div className="modal-body">
                Are You Sure ? You Want To Delete This Blog ??
              </div>
              <div className="modal-footer">
                <button
                  type="button"
                  className="btn btn-secondary"
                  data-bs-dismiss="modal"
                  onClick={() => handleRemoveBlog(selectedId)}
                >
                  YES
                </button>
                <button
                  type="button"
                  className="btn btn-primary"
                  data-bs-dismiss="modal"
                  aria-label="Close"
                >
                  No
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>
    ));

  return (
    <>
      <div className="justify-content-between m-5">
        <div className="row ">
          <div className="col-10">
            <h3 className="">Welcome {token.name.toUpperCase()}</h3>
          </div>
          <div className="col-2">
            <Link to="/LoginForm">
              <LogoutBtn />
            </Link>
          </div>
        </div>
      </div>
      <h1 style={{ color: "black" }} className="text-center m-3">
        Blogs
      </h1>
      <Link to="/AddBlog">
        <AddBtn
          style={{
            marginLeft: "145px",
            backgroundColor: "#1f813a",
            color: "white",
            marginTop: "15px",
          }}
        >
          Add Blog
        </AddBtn>
      </Link>
      <div className="text-center d-flex flex-wrap m-3 p-3 justify-content-center">
        {blogData !== "" && blogData !== null && blogData !== undefined ? (
          renderBlogs()
        ) : (
          <p>No Blogs</p>
        )}
      </div>
    </>
  );
};

export default BlogList;
