import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { useSelector, useDispatch } from "react-redux";
import { editBlog } from "../BlogReducer";
import TextField from "./TextField";
import AddBtn from "./AddBtn";

const EditBlog = () => {
  const params = useParams();
  const navigate = useNavigate();
  const dispatch = useDispatch();
  const blogs = useSelector((store) => store.blogs);

  const [values, setValues] = useState({
    title: "",
    content: "",
  });

  useEffect(() => {
    const blog = blogs.find((blog) => blog.id === parseInt(params.id));
    if (blog) {
      setValues({
        title: blog.title,
        content: blog.content,
      });
    }
  }, [blogs, params.id]);

  useEffect(() => {
    var blogs = JSON.parse(localStorage.getItem('blogs')) || [];

    // Find the user with the matching email
    var blog = blogs.find(function (blogs) {
      return blogs.title = values.title,  blogs.content = values.content;

    });
    localStorage.setItem("blogs", JSON.stringify(blog));
  }, []);

  const handleUpdateBlog = () => {
    dispatch(
      editBlog({
        id: parseInt(params.id),
        title: values.title,
        content: values.content,
      })
    );
    alert("Blog Updated!!");
    navigate("/BlogList");
  };

  return (
    <>
      <h1 className="text-center m-3">EDIT BLOG</h1>
      <form style={{ marginLeft: "554px", marginTop: "40px" }}>
        <div className="mb-3">
          <TextField
            label="Title: "
            value={values.title}
            onChange={(e) => setValues({ ...values, title: e.target.value })}
            inputs={{ type: "text" }}
          />
        </div>
        <div className="mb-3">
          <TextField
            label="Content: "
            value={values.content}
            onChange={(e) => setValues({ ...values, content: e.target.value })}
            inputs={{ type: "text" }}
          />
        </div>
        <div>
          <AddBtn
            style={{
              marginLeft: "64px",
              marginTop: "20px",
              backgroundColor: "#1070ef",
              color: "white",
              width: "99px",
            }}
            onClick={handleUpdateBlog}
          >
            Update
          </AddBtn>
        </div>
      </form>
    </>
  );
};

export default EditBlog;
