import { Link } from "react-router-dom";
import LogoutBtn from "./LogoutBtn";

const Blogs = () => {


  var data = JSON.parse(localStorage.getItem("blogs"));
  var users = JSON.parse(localStorage.getItem('users')) || [];
  var ur = sessionStorage.getItem("ce");
  //console.log(ur);

  var users = users.find(function (user) {
    return user.email === ur;
  });
  if(users){
   console.log(users.name);
  }








  const renderBlogs = () =>
    data.map((blog) => (
      <div key={blog.id}>
        <div className="card m-2" style={{ width: "20rem" }}>
          <div className="card-body">
            <h5 className="card-title">{blog.title}</h5>
            <p className="card-text">{blog.content}</p>
          </div>
        </div>
      </div>
    ));

  return (
    <>
      <h1 style={{ color: "black" }} className="text-center m-3">
        Blogs
      </h1>{" "}
      <Link to="/LoginForm">
        <LogoutBtn />
      </Link>
      <div className="text-center d-flex flex-wrap m-3 p-3 justify-content-center">
        {(data !== "" && data !== null && data !== undefined) ? renderBlogs() : "No Blogs"}
          {/* {renderBlogs() } */}
      </div>
    </>
  );
};

export default Blogs;
