import React, { useState } from "react";
import TextField from "./TextField";
import AddBtn from "./AddBtn";
import { useNavigate } from "react-router-dom";
import { useDispatch } from "react-redux";
import { addBlog } from "../BlogReducer";
import { Formik, Form } from "formik";
import * as yup from "yup";




const AddBlog = () => {

  const validates = yup.object().shape({
    name: yup
      .string()
      .required("Name Is Required")
      .max(10, "Must be 10 or less letters "),
    email: yup.string().email("invalid email").required("email is required"),
    password: yup
      .string()
      .required("Password is Required")
      .min(6, "At Least 6 Character"),
  });


  const dispatch = useDispatch();
  const navigate = useNavigate();
  const [values, setValues] = useState({
    title: "",
    content: "",
  });

  const handleAddBlog = (e) => {
    e.preventDefault();
    if (!values.title || !values.content) {
      alert("Please fill in all fields");
      return;
    }
    // Dispatching the new blog with the current values
    dispatch(
      addBlog({
        title: values.title,
        content: values.content,
      })
    );

    // Resetting the form values
    setValues({ title: "", content: "" });

    // Navigating to the BlogList page
    navigate("/BlogList");

    var blogs = JSON.parse(localStorage.getItem("blogs")) || [];
    blogs.push({
      title: values.title,
      content: values.content,
    });

    // Store the updated array back into localStorage
    localStorage.setItem("blogs", JSON.stringify(blogs));
  };

  return (
