  const blogs = [
    {
      title: "js",
      content: "jsssssss",
    },
    {
      title: "js",
      content: "jsssssss",
    },
    {
      title: "js",
      content: "jsssssss",
    },
    {
      title: "js",
      content: "jsssssss",
    },
    {
      title: "js",
      content: "jsssssss",
    },
    {
      title: "js",
      content: "jsssssss",
    },
  ];
  const blog = () =>
    blogs.map(blog => (
      <>

          <div className="card" style={{ width: "18rem" }}>
            <div className="card-body">
              <h5 className="card-title">{blog.title}</h5>
              <p className="card-text">{blog.content}</p>
              <a href="#" className="card-link">
                <AiOutlineDelete />
              </a>
              <a href="#" className="card-link">
                <CiEdit />
              </a>
            </div>
          </div>
      </>
    ));

  return (
    <>
      <h1 style={{ color: "black" }}>Blogs</h1>
      {/* <div className="row">

      </div> */}
      {/* <div className="blogs">
        <div></div>
      </div> */}
      <div className='text-center d-flex'>{blogs.length ? blog() : <p>NoBlogs</p>}</div>
    </>
  );
};

export default BlogList;
