import { AiOutlineDelete } from "react-icons/ai";
import { CiEdit } from "react-icons/ci";
import AddBtn from "./AddBtn";
import { Link } from "react-router-dom";
import LogoutBtn from "./LogoutBtn";
import { useEffect, useState } from "react";
import axios from "axios";

const BlogList = () => {
  // var userName = sessionStorage.getItem("name");
  // const [data, setData] = useState();
  // // const dispatch = useDispatch();
  // let selectedId = 0;

  const [blogData, setBlogData] = useState([]);

  useEffect(() => {
    getBlog();
  }, []);
console.log()
  const getBlog = async () => {
    axios
      .get("http://localhost:8081/api/blogList")
      .then((response) => {
        setBlogData(response.data);
      })
      .then((error) => {
        console.error(error);
      });
  };

  let selectedId = 0;

  const test = (id) => {
    selectedId = id;
  };


  const handleRemoveBlog = async (id) => {
    axios.delete(`http://localhost:8081/api/deleteBlog/${id}`);
    getBlog();
  };

  const renderBlogs = () =>
    blogData.map((blog) => (
      <div key={blog._id}>
        <div className="card m-2" style={{ width: "20rem" }}>
          <div className="card-body">
          <h5 className="card-img"><img src={`http://localhost:8081/uploads/${blog.blog_img}`} /> </h5>
            <h5 className="card-title">{blog.blog_title}</h5>
            <p className="card-text">{blog.blog_content}</p>
            {/* <img src='http://localhost:8081/uploads/sky.jpeg'  /> */}
            <Link
              to={`/updateBlog/${blog._id}`}
              style={{ margin: "15px", color: "green", fontSize: "20px" }}
            >
              <CiEdit />
            </Link>
            <Link
              style={{ color: "red", fontSize: "20px" }}
              data-bs-toggle="modal"
              data-bs-target="#exampleModal"
            >
              {/* onClick={() => handleRemoveBlog(blog._id)} */}
              <AiOutlineDelete     onClick={() => test(blog._id)}/>
            </Link> 
          </div>
        </div>
        <div
          className="modal fade"
          id="exampleModal"
          tabindex="-1"
          aria-labelledby="exampleModalLabel"
          aria-hidden="true"
        >
          <div className="modal-dialog">
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title" id="exampleModalLabel">
                  Delete BLog
                </h5>
                <button
                  type="button"
                  className="btn-close"
                  data-bs-dismiss="modal"
                  aria-label="Close"
                ></button>
              </div>
              <div className="modal-body">
                Are You Sure ? You Want To Delete This Blog ??
              </div>
              <div className="modal-footer">
                <button
                  type="button"
                  className="btn btn-secondary"
                  data-bs-dismiss="modal"
                  // onClick={() => handleRemoveBlog(blog._id)}
                  onClick={() => handleRemoveBlog(`${selectedId}`)}
                >
                  YES
                </button>
                <button
                  type="button"
                  className="btn btn-primary"
                  data-bs-dismiss="modal"
                  aria-label="Close"
                >
                  No
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>
    ));

  return (
    <>
      <div className="justify-content-between m-5">
        {/* <h3 className="ml-5">Welcome {userName}</h3> */}
        <Link to="/LoginForm">
          <LogoutBtn />
        </Link>
      </div>
      <h1 style={{ color: "black" }} className="text-center m-3">
        Blogs
      </h1>{" "}
      <Link to="/AddBlog">
        <AddBtn
          style={{
            marginLeft: "145px",
            backgroundColor: "#1f813a",
            color: "white",
            marginTop: "15px",
          }}
        >
          {" "}
          Add Blog
        </AddBtn>
      </Link>
      <div className="text-center d-flex flex-wrap m-3 p-3 justify-content-center">
        {blogData !== "" && blogData !== null && blogData !== undefined ? (
          renderBlogs()
        ) : (
          <p>No Blogs</p>
        )}
        {/* {renderBlogs()} */}
      </div>
    </>
  );
};

export default BlogList;
