import React, { useState } from "react";
import TextField from "./TextField";
import AddBtn from "./AddBtn";

const AddBlog = () => {

    const [values,setValues] = useState({
        title: '',
        content: ''
    })

    const handleAddBlog=()=>{
        console.log(values)
    }

  return (
    <>
      <h1 className="text-center m-3">ADD BLOG</h1>
      <form style={{ marginLeft: "554px", marginTop: "40px" }}>
        <div class="mb-3">
          <TextField label="Title: " value={values.title} onChange={(e)=>setValues({...values, title: e.target.value})} inputs={{ type: "text" }} />
        </div>
        <div class="mb-3">
          <TextField label="Content: " value={values.content} onChange={(e)=>setValues({...values, content: e.target.value})} inputs={{ type: "text" }} />
        </div>
        <div >
        <AddBtn style={{marginLeft: '64px', marginTop: '20px', backgroundColor : '#1070ef',color : "white" ,width:"99px"}} onClick={handleAddBlog}> Add</AddBtn>
        </div>

      </form>
    </>
  );
};

export default AddBlog;
