import TextField from "./TextField";
import AddBtn from "./AddBtn";
import { useNavigate } from "react-router-dom";
import { Formik, Form } from "formik";
import * as yup from "yup";
import axios from "axios";
import { useState } from "react";

const AddBlog = () => {
  const validates = yup.object().shape({
    blog_title: yup
      .string()
      .required("Title Is Required")
      .max(120, "Title Must be 120 or less letters ")
      .min(10),
    blog_content: yup
      .string()
      .required("Content Is Required")
      .max(520, "blog Must be 500 or less letters ")
      .min(10),
    // blog_img:yup.mixed().required("img is required")
  });

  const navigate = useNavigate();
  const [blogData, setBlogData] = useState({
    blog_title: "",
    blog_content: "",
  });
  const [image, setImage] = useState({ blog_img: null });

  // const handleAddBlog = async (e) => {
  //   try {
  //     const response = await axios.post("http://localhost:8081/api/addBlog", e,);
  //     console.log(response.data);
  //     setBlogData({
  //       blog_title: "",
  //       blog_content: "",
  //       blog_img: null,
  //     });

  //     console.log(response.data.blog_img);

  //     navigate("/BlogList");
  //   } catch (error) {
  //     console.error(error);
  //   }
  // };

  const handleAddBlog = async (e) => {
    try {
      const formData = new FormData();
      formData.append("blog_img", image);
      formData.append("blog_title", e.blog_title);
      formData.append("blog_content", e.blog_content);
      const response = await axios.post(
        "http://localhost:8081/api/addBlog",
        formData,   
        {
          headers: { "Content-Type": "multipart/form-data" },
        }
      );
      console.log(response.data);
      navigate("/BlogList");
    } catch (error) {
      console.error(error);
    }
    // try {
    //   const response = await axios.post("http://localhost:8081/api/addBlog", e);
    //   console.log(response.data);
    //   setBlogData({
    //     blog_title: "",
    //     blog_content: "",
    //     blog_img: "",
    //   });

    //   console.log(response.data.blog_img);

    //   navigate("/BlogList");
    // } catch (error) {
    //   console.error(error);
    // }
  };




  const onImageUpdate = (e) => {
    var render = new fileRender();
    render.readAsDataUrl(e.target.files[0]);
    render.onload = ()=>{
      console.log(render.result);
    };
    render.onerror = (error)=>{
      console.log("error",error);
    };

    setImage(e.target.files[0]);
  };

  // const handleInputChange = (e) => {
  //   const { name, value } = e.target;
  //   setBlogData({
  //     ...blogData,
  //     [name]: value,
  //   });
  // };

  return (
    <>
      <h1 className="text-center m-5">ADD BLOG</h1>
      <Formik
        initialValues={{
          blog_title: "",
          blog_content: "",
          blog_img: null,
        }}
        validationSchema={validates}
        // action="/upload" method="post" enctype="multipart/form-data"
        onSubmit={handleAddBlog}
      >
        {(Formik) => (
          <div className="container m-5">
            <Form>
              <div className="row justify-content-center">
                <div className="form-group col-md-4 col-md-offset-5 align-center ">
                  <TextField label="Title: " type="text" name="blog_title" />
                  <TextField
                    label="Content: "
                    type="text"
                    name="blog_content"
                  />
                  <TextField
                    label="Image: "
                    type="file"
                    name="blog_img"
                    onChange={onImageUpdate}
                  />
                  <div>
                    <AddBtn
                      // onClick={handleAddBlog}
                      style={{
                        marginLeft: "64px",
                        marginTop: "20px",
                        backgroundColor: "#1070ef",
                        color: "white",
                        width: "99px",
                      }}
                    >
                      {" "}
                      Add Blog
                    </AddBtn>
                  </div>
                </div>
              </div>
            </Form>
          </div>
        )}
      </Formik>
    </>
  );
};

export default AddBlog;
