import { configureStore } from "@reduxjs/toolkit";
import BlogReducer from "./BlogReducer";
import AuthReducer from './AuthReducer'

const store= configureStore({
    reducer:{
        auth : AuthReducer,
        blogs: BlogReducer,
    },   
})

export default store;
