import { createSlice } from "@reduxjs/toolkit";

const initialState=[
    {
        id: "1",
        title: "js",
        content: "jsssssss",
      },
      {
        id: "2",
        title: "js",
        content: "jsssssss",
      },
]

const BlogSlice = createSlice({
    name : "blogs",
    initialState,
    reducers:{
        addBlog: (state, action) => {
           state.push(action.payload)
        }
    }
})

export const {addBlog} = BlogSlice.actions
export default BlogSlice.reducer;
