import React from "react";
import ReactDOM from "react-dom/client";
import "./index.css";
import App from "./App";
import reportWebVitals from "./reportWebVitals";
import axios from "axios";

// http://localhost:8081/api/signup
const axiosInstance = axios.create({
  baseURL: "http://localhost:8081/api/signup",
});

axiosInstance.interceptors.request.use(
  (config) => {
    const token = JSON.parse(localStorage.getItem("login"));
    if (token) {
      config.headers.Authorization = `Bearer ${token.token}`;
    }
    return config;
  },
  (error) => {
    return Promise.reject(error);
  }
);

axios.interceptors.response.use(
  (response) => {
    console.log("RES", response);
    return response;
  },
  async (error) => {
    // if (error && error.response) {
    //   localStorage.removeItem("login");
    //   window.location = "/loginForm";
    //   console.log("ERR", error);
    //   console.log("ERR.RES", error.response);
    // }
    if (error.response && error.response.status === 419) {
      console.log("Token expired, refreshing...");
      try{
        const refreshToken = JSON.parse(localStorage.getItem("login")).refreshToken;
        if (!refreshToken) {
          console.log("No refresh token provided.");
          throw new Error("No refresh token provided.");
        }
        const response = await axiosInstance.get("http://localhost:8081/api/blogList", {refreshToken} );
          if (response.status === 200) {
            localStorage.setItem("login",JSON.stringify(response.data));
            console.log('token refresh');
            return axiosInstance(error.config);
          }
        }catch(refreshError){
          console.error("Error refreshing token:", refreshError);
        }
    }
    return Promise.reject(error);
  }
);

const root = ReactDOM.createRoot(document.getElementById("root"));
root.render(
  <React.StrictMode>
    <App />
  </React.StrictMode>
);

// If you want to start measuring performance in your app, pass a function
// to log results (for example: reportWebVitals(console.log))
// or send to an analytics endpoint. Learn more: https://bit.ly/CRA-vitals
reportWebVitals();
