import React, { useState } from "react";
import TextField from "./TextField";
import AddBtn from "./AddBtn";
import { useNavigate } from "react-router-dom";
import SelectField from "./SelectField";
// import { signUP } from "../AuthReducer";
// import { useDispatch } from "react-redux";
import { Formik, Form } from "formik";
import * as yup from "yup";

const SignupForm = () => {
  const validate = yup.object({
    name: yup
      .string()
      .required("Name Is Required")
      .max(10, "Must be 10 or less letters "),
    email: yup.string().email("invalid email").required("email is required"),
    password: yup
      .string()
      .required("Password is Required")
      .min(6, "At Least 6 Character"),
      // role: yup
      // .string()
      // .required("Role is Required")

    //confirmPassword: yup.string().oneOf([yup.ref('password'), null], 'Passwords must match'),
  });
  const navigate = useNavigate();
// const [values,setValues] = useState()
const [role,setRole] = useState()

  const options = [
    { value: "user", label: "User" },
    { value: "admin", label: "Admin" },
  ];

  const handleSignup = (e) => {
    e.preventDefault()
    navigate("/LoginForm");
    var users = JSON.parse(localStorage.getItem("users")) || [];
    // users.push({
    //   name: values.name,
    //   email: values.email,
    //   password: btoa(values.password),
    //   role: values.role,
    // });

    // Store the updated array back into localStorage
    localStorage.setItem("users", JSON.stringify(users));
  };

  return (
    <>
      <Formik
        initialValues={{
          name: "",
          email: "",
          password: "",
          role: "",
        }}
        validationSchema={validate}
      >
        {(Formik) => (
          <div className="container m-5">
            <Form>
              <div className="row justify-content-center">
                <div className="form-group col-md-4 col-md-offset-5 align-center ">
                  <h1 className="m-5 text-center">SignUp </h1>
                  <TextField label="Name: " type="text" name="name" />
                  <TextField label="Email: " type="email" name="email" />
                  <TextField
                    label="Password: "
                    type="password"
                    name="password"
                  />

                  <div className="mb-3">
                    <SelectField
                    name="role"
                      label={options.label}
                      options={options}
                      value={role}
                      id='userRole'
                      onChange={(e) =>
                        setRole({ ...values, role: e.target.value })
                      }
                    />
                  </div>
                  <div>
                    <AddBtn
                      style={{
                        marginLeft: "64px",
                        marginTop: "20px",
                        backgroundColor: "#1070ef",
                        color: "white",
                        width: "99px",
                      }}
                      onClick={handleSignup}
                    >
                      {" "}
                      SignUP
                    </AddBtn>
                  </div>
                </div>
              </div>
            </Form>
          </div>
        )}
      </Formik>

    </>
  );
};

export default SignupForm;
