import React, { useState } from "react";
import "./LoginForm.module.css";
import TextField from "./TextField";
import AddBtn from "./AddBtn";
import { useNavigate } from "react-router-dom";

const LoginForm = () => {

  const navigate = useNavigate();
  const [values, setValues] = useState({
    email: "",
    password: "",
  });

  const handleLogin = (e) => {
    e.preventDefault();
    if (!values.email || !values.password) {
      alert("Please fill in all fields");
      return;
    }
    var users = JSON.parse(localStorage.getItem('users')) || [];
    sessionStorage.setItem('ce', values.email)


    // Find the user with the matching email
    var user = users.find(function (user) {
      return user.email === values.email;

    });

    if (user) {
      // Check if passwords match
      if (user.password === btoa(values.password)) {
        alert('Login successful for ' + user.name + '!');
        if(user.role === 'user'){

          navigate('/Blogs')
        }else{
          navigate('/BlogList')
        }
      }
    }
  };



  return (
    <>
 <Formik
        initialValues={{
          name: "",
          email: "",
          password: "",
          role: "",
        }}
        validationSchema={validate}
      >
        {(Formik) => (
          <div className="container m-5">
            <Form>
              <div className="row justify-content-center">
                <div className="form-group col-md-4 col-md-offset-5 align-center ">
                  <h1 className="m-5 text-center">SignUp </h1>
                  <TextField label="Name: " type="text" name="name" />
                  <TextField label="Email: " type="email" name="email" />
                  <TextField
                    label="Password: "
                    type="password"
                    name="password"
                  />

                  <div className="mb-3">
                    <SelectField
                      label={options.label}
                      options={options}
                      value={values.role}
                      onChange={(e) =>
                        setValues({ ...values, role: e.target.value })
                      }
                    />
                  </div>
                  <div>
                    <AddBtn
                      style={{
                        marginLeft: "64px",
                        marginTop: "20px",
                        backgroundColor: "#1070ef",
                        color: "white",
                        width: "99px",
                      }}
                      onClick={handleSignup}
                    >
                      {" "}
                      SignUP
                    </AddBtn>
                  </div>
                </div>
              </div>
            </Form>
          </div>
        )}
      </Formik>
    </>
  );
};

export default LoginForm;
